import React, { useState } from 'react';
import { View, Text, TextInput, Button, StyleSheet, AsyncStorage } from 'react-native';

const Login = ({ navigation }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [ownership, setOwnership] = useState('Select Ownership');

  const isEmailValid = (email) => {
    // Regular expression for basic email validation
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
  };

  const handleLogin = async () => {
    try {
      // Validate email format
      if (!isEmailValid(email)) {
        console.error('Invalid email format');
        return;
      }

      // Connect to MongoDB and verify user credentials
      const user = await usersCollection.findOne({ email, password });
      if (!user) {
        console.error('Invalid credentials');
        return;
      }

      // Create JWT token
      const token = jwt.sign({ username: user.username }, process.env.JWT_SECRET, {
        expiresIn: '1h',
      });

      // Store token in local storage and navigate to home screen
      await AsyncStorage.setItem('token', token);
      navigation.navigate('Home');
    } catch (error) {
      console.error(error.message);
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.header}>Login</Text>
      <TextInput
        style={styles.input}
        placeholder="Email"
        onChangeText={(text) => setEmail(text)}
        value={email}
      />
      <TextInput
        style={styles.input}
        placeholder="Password"
        secureTextEntry
        onChangeText={(text) => setPassword(text)}
        value={password}
      />
      <View style={{ flexDirection: 'row' }}>
        <Text style={styles.forgotPassword} onPress={() => navigation.navigate('ForgotPassword')}>
          Forgot Password?
        </Text>
        <Text style={styles.register} onPress={() => navigation.navigate('Register')}>
          Register
        </Text>
      </View>

      <Button title="Login" onPress={handleLogin} />
    </View>
  );
};

const styles = StyleSheet.create({
  // ... (your existing styles)
});

export default Login;
